//    ));
const name = decks.map(({name}) => <div className="card-title">{name}</div>)
  const cardsInfo = cards.map(({deckId, front, back}) => (


          <div className="card">
              <div className="card-body">{front}</div>
          </div>
      ))

   return <>{cardsInfo}</>

}
