import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { listCards } from "../utils/api"

export default function Study({ decks }){
    const [cards, setCards] = useState([]);
    const { deckId } = useParams();

    useEffect(() => {
        async function getCards(){
            const cardsFromAPI = await listCards(deckId);
            setCards(cardsFromAPI);
          }

          getCards();
    },[deckId]);

   console.log(decks)
   const deckName = decks.map(deck => {
       cards.map(card => {
           console.log(card.deckId)
           console.log(deck.id)
           if(card.deckId == deck.id){
                  return <h1>{deck.name}: Study</h1>
           }
       })
   });
    if(cards.length <= 2 ){
        return  (
            <div>
            {deckName}
            <h3>Not Enough cards.</h3>
            <p>You need at least 3 cards to study. There are {cards.length} in this deck</p>
            <button className="btn btn-primary fas fa-plus"> Add Cards</button>
            </div>
        )
    } else {
        return null
    }

}
