  useEffect(() => {
    async function getDecks(){
        const deckFromAPI = await listDecks();
        setDecks(deckFromAPI);
    }
    getDecks();
},[]);

  return (
    <>
      <Header />
      <div className="container">
        {/* TODO: Implement the screen starting here */}
        <Switch>
        <Route exact={true} path={"/"}>
          <Decks decks  />
        </Route>
        <Route>
          <NotFound />
        </Route>
        </Switch>
      </div>
    </>
  );
}

export default Layout;
